mapper.select:
    select a.storeid, a.areaid,b.subject,0 as result,count(b.result) as count from t_student a join  t_orderexam_item b on a.id=b.studentid 
    where subject in (1,3,5,7) and result=0

		and examdate > #{begindate}


		and examdate <= #{enddate} 


		and b.subject = #{subjectid}


		and a.areaid = #{areaid}


		and a.storeid = #{storeid}


    group by a.storeid,b.subject

    select a.storeid, a.areaid,b.subject,result, count(b.subject) as count from t_student a join t_trainexam_item  b on a.id=b.studentid 
    where subject in (1,3,5,7) 

		and examdate > #{begindate}


		and examdate <= #{enddate} 


		and b.subject = #{subjectid}


		and a.areaid = #{areaid}


		and a.storeid = #{storeid}

    group by a.classid,a.areaid,b.subject,b.result
