mapper.select:
select

from t_resultexam_item


        tableid = #{tableid,jdbcType=VARCHAR}


        idcard = #{idcard,jdbcType=VARCHAR}


        studentid = #{studentid,jdbcType=INTEGER}


        subject = #{subject,jdbcType=INTEGER}


        result = #{result,jdbcType=INTEGER}


order by ctime desc

select a.tableid, a.name, a.studentid, a.schoolname, a.traintype, a.idcard, a.flownum, a.indexcat, a.result, a.subject, a.examdate, a.examcount, a.examtime, a.examplaceid, a.cname, a.ctime
from t_resultexam_item a join t_coach_student b on a.studentid=b.studentid where coachid = #{coachid}
order by a.ctime desc

select t.tableid, t.examdate, t.examtime, t.areaid, t.ctime, (select NAME from t_area where id=t.areaid) as areaName, t.storeid, (select name from t_store where id=t.areaid) as storeName, cs.coachid, (select c.`name` from t_coach c where c.coachid=cs.coachid ) AS coachName, t.subject, t.indexcat, count(t.examcount) examcount, t.examplaceid, count(t.studentid) as nnt, count(t.examcount)/count(t.studentid) as percentage from t_resultexam_item t LEFT JOIN t_coach_student cs on cs.studentid=t.studentid and cs.isvalid=1 LEFT JOIN t_student s on t.studentid=s.id where 1=1
and t.storeid = #{storeId}
and t.traintype = #{traintype}
and t.subject = #{subject}
and t.result = #{result}
and t.examplaceid = #{examplaceid}
and t.examcount = #{examcount}
and t.indexcat = #{indexcat}
and date(examdate) between #{startTime} and #{endTime}
and cs.coachid = #{coachId}
and s.classid = #{classId}
group by t.areaid
group by t.storeid
group by coachid
group by t.examplaceid
group by t.indexcat
group by t.examcount

select t.tableid, t.examdate, t.examtime, t.areaid, t.ctime, (select NAME from t_area where id=t.areaid) as areaName, t.storeid, (select name from t_store where id=t.areaid) as storeName, cs.coachid, (select c.`name` from t_coach c where c.coachid=cs.coachid ) AS coachName, t.subject, t.indexcat, t.examplaceid, count(t.studentid) as nnt from t_resultexam_item t LEFT JOIN t_coach_student cs on cs.studentid=t.studentid and cs.isvalid=1 LEFT JOIN t_student s on t.studentid=s.id LEFT JOIN t_coach c on c.coachid=cs.coachid where 1=1
and t.storeid = #{storeId}
and t.areaid = #{areaId}
and t.traintype = #{traintype}
and t.subject = #{subject}
and t.examplaceid = #{examplaceid}
and t.indexcat = #{indexcat}
and date(examdate) between #{startTime} and #{endTime}
and cs.coachid = #{coachId}
and s.classid = #{classId}
and s.classid = #{classId}
group by t.areaid
group by t.storeid
group by coachid
group by t.examdate
group by c.headcoachid

select t.areaid, (select NAME from t_area where id=t.areaid) as areaName, t.storeid, (select name from t_store where id=t.areaid) as storeName, c.headcoachid, (select cc.name from t_coach cc where cc.coachid=c.headcoachid) as headCoachName, cs.coachid, (select c.`name` from t_coach c where c.coachid=cs.coachid ) AS coachName, t.subject, t.examplaceid, t.traintype, s.classid, (select ci.`name` from t_classinfo ci where ci.id=s.classid) as className, t.subject-1 as upsubject, t.examdate, count(t.studentid) as nnt, datediff(NOW(),examdate) as waitDay from t_resultexam_item t LEFT JOIN t_coach_student cs on t.studentid=cs.studentid LEFT JOIN t_student s on t.studentid=s.id LEFT JOIN t_coach c on c.coachid=cs.coachid where 1=1
and t.storeid = #{storeId}
and t.traintype = #{traintype}
and t.subject = #{subject}
and t.examplaceid = #{examplaceid}
and t.indexcat = #{indexcat}
and date(examdate) between #{startTime} and #{endTime}
and cs.coachid = #{coachId}
and s.classid = #{classId}
and s.classid = #{classId}
group by t.areaid
group by t.storeid
group by coachid
group by t.examdate
group by c.headcoachid

select t.tableid, t.areaid, (select NAME from t_area where id=t.areaid) as areaName, t.storeid, (select name from t_store where id=t.areaid) as storeName, t.result, (SELECT result FROM `t_resultexam_item` where studentid=t.studentid order by examdate desc limit 1,1) as upResult, c.headcoachid, (select cc.name from t_coach cc where cc.coachid=c.headcoachid) as headCoachName, cs.coachid, (select c.`name` from t_coach c where c.coachid=cs.coachid ) AS coachName, t.subject, t.examplaceid, t.traintype, s.classid, (select ci.`name` from t_classinfo ci where ci.id=s.classid) as className, t.subject-1 as upsubject, t.examdate, (SELECT examdate FROM `t_resultexam_item` where studentid=t.studentid order by examdate desc limit 1,1) as upDay, t.name, t.idcard, datediff(NOW(),examdate) as waitDay from t_resultexam_item t LEFT JOIN t_coach_student cs on t.studentid=cs.studentid LEFT JOIN t_student s on t.studentid=s.id LEFT JOIN t_coach c on c.coachid=cs.coachid where 1=1
and t.subject = #{subject}
and cs.coachid = #{coachId}
